scwin.sub_ST0604G1R0_submitdone = function(e) {
		com.setFocusedCell(ST0604G1, "chk");
		ST0604G1.setCellChecked(ST0604G1.getFocusedRowIndex(), "chk", true);

		var chkJsonData = ST0604G1.getCheckedJSON("chk");
		var chkCnt = chkJsonData.length;
		var i = 0;
		var nodeId = [];

		com.setFocusedCell(ST0604G1, "NODE_ID");

		var date = new Date();
		var year = date.getFullYear();
		var month = date.getMonth()+1;
		var day = date.getDate();

		//체크 가져오기
		for(i; i<chkCnt; i++){
			nodeId.push(chkJsonData[i].NODE_ID);
		}

		dma_sub_search.set("NODE_ID", nodeId);

		com.executeSubmission(sub_ST0604G1R1);

};

scwin.ST0604G1_oncellclick = function(row,col,colId) {
	if (col != "0"){
	   if(ST0604G1.getCellChecked(row, 'chk')==0){
	      ST0604G1.setCellChecked(row, 'chk', true);
	   }else if(ST0604G1.getCellChecked(row, 'chk')==1){
	      ST0604G1.setCellChecked(row, 'chk', false);
	   }
	}

	var chkJsonData = ST0604G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var nodeId = [];

	//체크된 데이터 가져오기
	for(i; i<chkCnt; i++){
		nodeId.push(chkJsonData[i].NODE_ID);
	}

	dma_sub_search.set("NODE_ID", nodeId);

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0604G1R1);
		//pivot_ST0604.show();
	}else if(chkJsonData.length == 0){
		chart_ST0604.destroy();
		//pivot_ST0604.hide();
	}


};

