scwin.onpageload = function() {

	var autoOptions = {
		Main : { grid : ST0801G0, srchSbm : sub_ST0801G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"},
		Sub1 : { grid : ST0801G1, srchSbm : sub_ST0801G1R0, keyColumn : "CRS_ID", srchGrp : pgm_header, name :  "교차로 목록"}
	 };		

	  var mainOptions = {
         EXL:scwin.btn_excel_onclick
      };

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID_FORM, autoOptions, mainOptions);

	com.executeSubmission(sub_ST0801SHI0);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var today = year+month+day;

	F_DATE.setValue(today);
	L_DATE.setValue(today);
	dma_sub_search.set("TAB_DIV", "AVRG_DELAY_SEC");

	$("#ST0801_stat").attr("style", "width:202px; margin-top: 10px;");

	//탭 목록 클릭 이벤트
	$(".stat_sgn").find("li").click( function() {
		var li_id = this.id;
		$(".stat_sgn").find("li").removeClass("on");
		$(".stat_sgn").find("#"+li_id).addClass("on");
		if(li_id == "li_avrg_delay_sec") {
			dma_sub_search.set("TAB_DIV", "AVRG_DELAY_SEC");
			pivot_ST0801.setVals("AVRG_DELAY_SEC");
			pivot_ST0801_sub.setVals("AVRG_DELAY_SEC");
			com.executeSubmission(sub_ST0801G1R1);
		}
		else if(li_id == "li_psig_cnt") {
			dma_sub_search.set("TAB_DIV", "PSIG_CNT");
			pivot_ST0801.setVals("PSIG_CNT");
			pivot_ST0801_sub.setVals("PSIG_CNT");
			com.executeSubmission(sub_ST0801G1R1);
		}
	});


};

