scwin.sub_ST0801G1R0_submitdone = function(e) {
	com.setFocusedCell(ST0801G1, "chk");
	ST0801G1.setCellChecked(ST0801G1.getFocusedRowIndex(), "chk", true);

	var chkJsonData = ST0801G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var crsId = [];

	com.setFocusedCell(ST0801G1, "CRS_ID");

	var date = new Date();
	var year = date.getFullYear();
	var month = date.getMonth()+1;
	var day = date.getDate();

	//체크 가져오기
	for(i; i<chkCnt; i++){
		crsId.push(chkJsonData[i].CRS_ID);
	}

	//조회구분도 넣어서 처리하기. 일별 월별 시간대별
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());
	dma_sub_search.set("CRS_ID", crsId);

	com.executeSubmission(sub_ST0801G1R1);
};

scwin.ST0801G1_oncellclick = function(row,col,colId) {
	if (col != "0"){
	   if(ST0801G1.getCellChecked(row, 'chk')==0){
	      ST0801G1.setCellChecked(row, 'chk', true);
	   }else if(ST0801G1.getCellChecked(row, 'chk')==1){
	      ST0801G1.setCellChecked(row, 'chk', false);
	   }
	}

	var chkJsonData = ST0801G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var crsId = [];

	//체크된 데이터 가져오기
	for(i; i<chkCnt; i++){
		crsId.push(chkJsonData[i].CRS_ID);
	}

	dma_sub_search.set("CRS_ID", crsId);
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0801G1R1);
		pivot_ST0801.show();
	}else if(chkJsonData.length == 0){
		chart_ST0801.destroy();
		pivot_ST0801.hide();
	}


};

scwin.ST0801G1_onheaderclick = function(chkHeader) {
