	var chkCnt = chkJsonData.length;
	var i = 0;
	var crsId = [];

	//체크 가져오기
	for(i; i<chkCnt; i++){
		crsId.push(chkJsonData[i].CRS_ID);
	}

	dma_sub_search.set("CRS_ID", crsId);

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0801G1R1);
		pivot_ST0801.show();
	}else if(chkJsonData.length == 0){
		chart_ST0801.destroy();
		pivot_ST0801.hide();
	}
};

scwin.getRandomColor = function(_isAlpha) {
	  var r = getRand(0, 255),
	  g = getRand(0, 255),
	  b = getRand(0, 255),
	  a = getRand(0, 10) / 10;

	  var rgb = _isAlpha ? 'rgba' : 'rgb';
	  rgb += '(' + r + ',' + g + ',' + b;
	  rgb += _isAlpha ? ',' + a + ')' : ')';

	  return rgb;

	  // Return random number from in to max
	  function getRand(min, max) {
	    if (min >= max) return false;
	    return ~~(Math.random() * (max - min + 1)) + min;
	  };
};


var CharData = function(){
	this.seriesname = {};
	this.fill = {}; //선 아래 여백
	this.backgroundColor = {};
	this.borderColor = {};
	this.data = [];
	this.borderWidth = {}; //선 width
	this.pointBorderWidth = {}; //포인터 width
	this.tension = {};	//곡선 or 직선
	this.pointRadius = {};	//포인트 반지름 0일 경우 포인트 안그려짐
}


var chart_ST0801 = null;

scwin.DrawLineChart_Time = function(chkCnt, chkJsonData){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_SGN_STAT_PIVOT_2.getRowCount();
	var jsonData = dlt_BRT_SGN_STAT_PIVOT_2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();

		for(j; j<rowCount; j++){
			if(jsonData[num].CRS_ID ==  dlt_BRT_SGN_STAT_PIVOT_2.getCellData(j, "CRS_ID")){
				transData.push(dlt_BRT_SGN_STAT_PIVOT_2.getRowJSON(j));
			}else if(jsonData[num].CRS_ID  !=  dlt_BRT_SGN_STAT_PIVOT_2.getCellData(j, "CRS_ID")){
				num = j;
				j = j;
				break;
			}
		}


		for(var y = 0; y < transData.length; y++){
			var label = transData[y].CRS_NM;

			if(dma_sub_search.get("TAB_DIV") == 'AVRG_DELAY_SEC'){
				var val = transData[y].AVRG_DELAY_SEC;
			}else if(dma_sub_search.get("TAB_DIV") == 'PSIG_CNT'){
				var val = transData[y].PSIG_CNT;
			}

			var statH = transData[y].STAT_H;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = val;
				labelJson = statH;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};
	var charData = new CharData();
	var rgb = scwin.getRandomColor();

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1;
	charData.pointBorderWidth = 1;
	charData.tension = 0;
	charData.pointRadius = 1;


	datasets.push(charData);

	transData = [];
}

	if(chart_ST0801 != null) {
	      chart_ST0801.destroy();   
	}

	var ctx = document.getElementById('chart_ST0801');
	chart_ST0801 = new Chart(ctx, {
		    type: 'line',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,

		    }
		});

}

