};

scwin.sub_ST0801G2R0_submitdone = function(e) {
	var chkJsonData = ST0801G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	chart_ST0801.destroy();

	if(chkCnt > 0 && SELECT_DIV.getValue() == 'TIME'){
		scwin.DrawLineChart_Time(chkCnt, chkJsonData);
		ST0801T0.show();
		ST0801T1.hide();
	}else if(chkCnt > 0 && SELECT_DIV.getValue() == 'DAY'){
		scwin.DrawBarChart_Day(chkCnt, chkJsonData);
		ST0801T0.hide();
		ST0801T1.show();
	}else if(chkCnt > 0 && SELECT_DIV.getValue() == 'MONTH'){
		scwin.DrawBarChart_Month(chkCnt);
		ST0801T0.hide();
		ST0801T1.show();
	}

		//pivot테이블 옵션 따로 설정.
		/*pivot_ST0801.setRows("STTN_NM");
		pivot_ST0801.setCols("STAT_H");
		pivot_ST0801.setVals("AVRG_DELAY_SEC");*/
};
