head.script:
	var autoOptions = {};

	scwin.onpageload = function() {
		autoOptions = {
			Main : { grid : VH0200G0, srchSbm : sub_VH0200G0R0,
					 srchGrp : pgm_header , keyColumn : "ROUT_ID,OCR_DTM,NODE_TYPE", focusColumn : "ROUT_ID", name : "변경운행 이력"},
			Sub1 : { grid : VH0200G1, srchSbm : sub_VH0200G1R0, keyColumn : "OPER_SN", name : "변경운행이력 상세" }	
		 };

		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions);

		var subOptions2 = {
			SEARCH:{cbFnc:scwin.openPopUpChart,nm:"변경운행 시공도 보기",class:"search"}, EXL:{}
		};

		com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);

		dma_search.F_DATE = util.getCurrentDate2(-7);
		F_DATE.setValue(util.getCurrentDate2(-7));

		dma_search.L_DATE = util.getCurrentDate2();
		L_DATE.setValue(util.getCurrentDate2());

		var codeOptions = [ 
							{code : "CHG_OPER_DIV", compID : "VH0200G0:CHG_OPER_DIV" }

						];		

		com.setCommonDtl(codeOptions, function() {
			com.searchGrid(VH0200G0, sub_VH0200G0R0);
		});	

		com.executeSubmission(sub_repRoutItem);
		com.executeSubmission(sub_routItem);	
		com.executeSubmission(sub_vhcItem);	

		//VH0200G0.mergeCells("byrestriccol");

	};

	scwin.onpageunload = function() {

	};

	scwin.setGridViewInfo = function() {

		var gridHeader ="", gridBody="";
		var rowData = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getAllJSON();
		var rowCntArr = dlt_BRT_CUR_OPER_ALLOC_PL_NODE_INFO_HIS_CNT.getAllJSON();

		var maxCnt = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			if(maxCnt < rowCntArr[i].CNT)
				maxCnt = rowCntArr[i].CNT;
		}

		if(maxCnt==0)maxCnt=30;

		if(rowData.length == 0 ) {
			dma_sub_search.set("NODE_TYPE", "NT002"); 
			com.executeSubmission(sub_MAIN_ROUT_NODE_CPMSTN);
			return;
		}

		dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.removeAll();

		gridHeader +='<w2:row style="" id="first_row">';
		gridHeader +='<w2:column blockSelect="false" id="'+'h'+'OPER_SN'+'" style="height:20px" inputType="text" width="70" value="'+'운행회차'+'" rowSpan="3"></w2:column>';
		for(var i = 0; i < maxCnt; i++) {
			var nodeNm = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(i, "NODE_NM");
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'NODE_NM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="4" value="'+nodeNm+'"></w2:column>';
		}
		gridHeader +='</w2:row>';

		gridBody += '<w2:column blockSelect="false" id="'+'OPER_SN'+'" style="height:20px" inputType="text" width="70"></w2:column>';


		gridHeader +='<w2:row style="" id="second2_row">';
		for(var i = 0; i < maxCnt; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ALLOC_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="운행계획"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'CHG_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="변경운행결과"></w2:column>';
		}
		gridHeader +='</w2:row>';


		gridHeader +='<w2:row style="" id="second_row">';
		//maxCnt만큼 body 생성  
		for(var i = 0; i < maxCnt; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'CHG_ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'CHG_DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridBody += '<w2:column blockSelect="false" id="'+'ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'CHG_ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'CHG_DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
		}
		gridHeader +='</w2:row>';

		var rowIndex = 0;
		var rowCnt = 0;


		for(var i = 0; i < rowCntArr.length; i++) {
			var j = 0;
			rowIndex += rowCnt;
			rowCnt =  rowCntArr[i].CNT;
			var columnVal = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(rowIndex+j, "OPER_SN");
			if(com.isEmpty(columnVal))break;
			var idx = dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.insertRow();
			dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.setCellData(idx, "OPER_SN", columnVal);

			for(j = 0; j < rowCnt; j++) {
				var nodeStTm = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(rowIndex+j, "DPRT_TM");
				var nodeEdTm = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(rowIndex+j, "ARRV_TM");
				dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.setCellData(idx, "ARRV_TM_"+j, nodeEdTm);
				dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.setCellData(idx, 'DPRT_TM_'+j, nodeStTm);

				var chgDprtTm = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(rowIndex+j, "CHG_DPRT_TM");
				var chgArrvTm = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(rowIndex+j, "CHG_ARRV_TM");
				dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.setCellData(idx, "CHG_ARRV_TM_"+j, chgArrvTm);
				dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.setCellData(idx, "CHG_DPRT_TM_"+j, chgDprtTm);

			}
		}

		//gridView 정보 정의 
		//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
		var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" dataList="dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS" '
						+ 'scrollByColumn="true" id="VH0200G1" ev:onrowindexchange="scwin.VH0200G1_onrowindexchange" class ="cm_grid right" fixedColumnWithHidden="true" useShiftKey="false" mergeCells="byrestriccol" readOnly="true" mergeCol="OPER_SN, WAY_DIV" >'
		+ '<w2:header id="header1" style="text-align:left">'
		+ gridHeader
		+'</w2:header>'
		+'<w2:gBody id="gBody1" style="">'
		+'<w2:row id="row2" style="">'
		+ gridBody
		+'</w2:row>'
		+'</w2:gBody>'
		+'</w2:gridView>' ;

		VH0200G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = VH0200G1;
		VH0200G1.setFixedColumn(1);


		dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.modifyAllStatus("R")
	};

    //빈 그리드 그리기(FOR 엑셀)
