	scwin.openPopUpChart = function(){


		var jsonData = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getAllJSON();
		var transData = new Array();
		var realData = new Array();


		for(var j=0; j<dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowCount(); j++){
			if(jsonData[j].OPER_SN  == dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.getCellData(VH0200G1.getFocusedRowIndex(), "OPER_SN")){
				transData.push(dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(j));
				realData.push(dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(j));
			}
		}

		//var routId = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "ROUT_ID");
		//var dayDiv = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "DAY_DIV");
		//var routNm = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "ROUT_NM");
		var operSn = dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.getCellData(VH0200G1.getFocusedRowIndex(), "OPER_SN");

		 var data = { data4 : operSn, data5:transData, data6:realData, callbackFn : "" };
         var options = { id : "VH0200P0",
         				 width : "1600px",
         				 height : "900px",
                         popupName : "변경 운행 시공도", 
                         modal : true};

		com.openPopup("/cm/common/popUpChgCompareChart.xml", options, data);
	};



	scwin.sbx_ChoiceBox_onviewchange = function(info) {
		if (info.newValue == "ALL") {
			autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} 
		else if (info.newValue == "REP_ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_ROUT_NM", "REP_ROUT_NM");
			autoChoiceBox.show("");
		} else if (info.newValue == "ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "ROUT_NM", "ROUT_NM");
			autoChoiceBox.show("");
		}
	};

	scwin.sub_VH0200G0R0_submitdone = function(e) {
		com.setFocusedCell(VH0200G0, "OCR_DTM");
	};

	scwin.VH0200G0_onrowindexchange = function(row,oldRow) {

		var ocrDtm = dlt_BRT_CUR_CHG_OPER_INFO_HIS.getCellData(row, "OCR_DTM");

		dma_sub_search.set("ROUT_ID", dlt_BRT_CUR_CHG_OPER_INFO_HIS.getCellData(row, "ROUT_ID"));
		dma_sub_search.set("OCR_DTM", ocrDtm);
		com.executeSubmission(sub_VH0200G1CNT);
		com.executeSubmission(sub_VH0200G1R0);


	};

	scwin.sub_VH0200G1R0_submitdone = function(e) {
		com.setFocusedCell(VH0200G1, "OPER_SN");
		scwin.setGridViewInfo();
	};

	scwin.VH0200G1_onrowindexchange = function(row,oldRow) {
	};

	scwin.sub_MAIN_ROUT_NODE_CPMSTN_submitdone = function(e) {
		scwin.dispEmptyGridView();
	};

	scwin.sub_repRoutItem_submitdone = function(e) {
		com.executeSubmission(sub_routItem);
	};

	scwin.TYPE1_onviewchange = function(info) {	
		com.executeSubmission(sub_routItem);
		com.searchGrid(VH0200G0, sub_VH0200G0R0);
	};

	scwin.TYPE2_onviewchange = function(info) {		
		com.searchGrid(VH0200G0, sub_VH0200G0R0);
	};





	scwin.F_DATE_onviewchange = function(info) {
		var ldate = L_DATE.getValue();
		var fdate = F_DATE.getValue();

		if (fdate > ldate && ldate != ""){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseF(info));
		}
	};
	scwin.checkCLoseF = function(info) {
		F_DATE.setValue(info.oldValue);
	}
	scwin.checkCLoseL = function(info) {
		L_DATE.setValue(info.oldValue);
	}	
	scwin.L_DATE_onviewchange = function(info) {
		var ldate = L_DATE.getValue();
		var fdate = F_DATE.getValue();

		if (fdate > ldate){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseL(info));
		}
	};
