			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ALLOC_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="운행계획"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'CHG_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="변경운행결과"></w2:column>';
		}
		gridHeader +='</w2:row>';


