			gridBody += '<w2:column blockSelect="false" id="'+'CHG_ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'CHG_DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
