		+'<w2:row style="" id="row4">'
		+'<w2:column removeBorderStyle="false" width="NaN" inputType="expression" style="height:20px;" '
		+'id="column55" value="" blockSelect="false" displayMode="label" colSpan="100" rowSpan="" '
		+'expression="&quot;데이터 개수 : &quot; + COUNT(&apos;COMP_ID&apos;)" textAlign="left">'
		+'</w2:column>'
		+'</w2:row>'
		+'</w2:footer>'

		+'</w2:gridView>' ;

		VH0200G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = VH0200G1;
		VH0200G1.setFixedColumn(1);

		dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.modifyAllStatus("R");

		for(var i = 0; chgRow.length > i; i++){
			VH0200G1.setRowColor(chgRow[i], 'red');
		}
		VH0200G1.setFocusedCell(firstIndex, "OPER_SN", false);

		/*var rowCnt = dlt_BRT_CUR_OPER_ALLOC_PL_NODE_INFO_HIS_CNT.getRowCount();
