	scwin.openPopUpChart = function(){
		var jsonData = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getAllJSON();
		var transData = new Array();
		var realData = new Array();

		for(var j=0; j<dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowCount(); j++){
			if(jsonData[j].OPER_SN  == dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.getCellData(VH0200G1.getFocusedRowIndex(), "OPER_SN")){
				transData.push(dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(j));
				realData.push(dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(j));
			}
		}

		//var routId = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "ROUT_ID");
		//var dayDiv = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "DAY_DIV");
		//var routNm = dlt_BMS_ROUT_MST.getCellData(VH0200G0.getFocusedRowIndex(), "ROUT_NM");
		var operSn = dlt_VIEW_CUR_OPER_ALLOC_PL_NODE_INFO_HIS.getCellData(VH0200G1.getFocusedRowIndex(), "OPER_SN");

		 var data = { data4 : operSn, data5:transData, data6:realData, callbackFn : "" };
         var options = { id : "VH0200P0",
         				 width : "1600px",
         				 height : "800px",
                         popupName : "변경 운행 시공도", 
                         modal : true};

		com.openPopup("/cm/common/popUpChgCompareChart.xml", options, data);
	};

	scwin.sbx_ChoiceBox_onviewchange = function(info) {
		if (info.newValue == "ALL") {
			autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} 
		else if (info.newValue == "REP_ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_ROUT_NM", "REP_ROUT_NM");
			autoChoiceBox.show("");
		} else if (info.newValue == "ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "ROUT_NM", "ROUT_NM");
			autoChoiceBox.show("");
		}
	};

