	scwin.sub_VH0200G1R0_submitdone = function(e) {
		com.setFocusedCell(VH0200G1, "OPER_SN");

		var rowCnt = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowCount();
		var oldNodeSn = 0;
		var oldOperSn = 0;
		var curOperSn = 0;
		var curNodeSn = 0;
		var newOperSn = 0;
		var newNodeSn = 0;
		var chgDivCnt = 0;

		for(var i = 0; i < rowCnt; i++){
			var chgDiv = 0;
			if(i>= 1) {
				oldNodeSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(i-1, 'NODE_SN');
				oldOperSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(i-1, 'OPER_SN');
			}

			curNodeSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(i, 'NODE_SN');
			curOperSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(i, 'OPER_SN');

			var rowJson = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(i);
			dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setCellData(i, "CHG_DIV", chgDiv);
			//dlt_VIEW_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setRowJSON(i, rowJson, false);

			if(oldNodeSn != '' && curNodeSn != '' && oldNodeSn >= curNodeSn) {
				//2번 이상 변경운행 발생
				if(oldOperSn == curOperSn) {
					for(var j = 0; j < rowCnt; j++){
						newNodeSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(j, 'NODE_SN');
						newOperSn = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getCellData(j, 'OPER_SN');

						if(newOperSn != '' && curOperSn != '' && newOperSn == curOperSn && newNodeSn == curNodeSn){
							dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setRowJSON(j, dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowJSON(i), true);
							chgDiv++;
							chgDivCnt = chgDivCnt + chgDiv;
							dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setCellData(j, "CHG_DIV", chgDivCnt);
						}
					}
					dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.removeRow(i);
					i--;
				}
			}
		}
		dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.modifyAllStatus("R");
		//동적그리드 함수
		scwin.setGridViewInfo();
	};

