	scwin.F_DATE_VH0200_onviewchange = function(info) {
		var ldate = L_DATE_VH0200.getValue();
		var fdate = F_DATE_VH0200.getValue();

		if (fdate > ldate && ldate != ""){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseF(info));
		}
	};
	scwin.checkCLoseF = function(info) {
		F_DATE_VH0200.setValue(info.oldValue);
	}
	scwin.checkCLoseL = function(info) {
		L_DATE_VH0200.setValue(info.oldValue);
	}	
	scwin.L_DATE_VH0200_onviewchange = function(info) {
		var ldate = L_DATE_VH0200.getValue();
		var fdate = F_DATE_VH0200.getValue();

		if (fdate > ldate){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.",scwin.checkCLoseL(info));
		}
	};

	//라디오 버튼에 따라 날짜 설정
	scwin.calendar_radio_onviewchange = function(info) {
		var date = new Date();
		var year = date.getFullYear().toString();
		var month = (date.getMonth()+1).toString();
		var day = date.getDate().toString();
		var zero = 0;

		//글자수 맞추기
		if(day.length == 1){
			day = zero + day;
		}

		if(month.length == 1){
			month = zero + month;
		}

		var weekAgoDay = parseInt(day) - 7;
		var todaysDate = year+month+day;
		var weekAgoDate = year+month+weekAgoDay;
		var firstday = year+month+"01";		

		if(info.value == "DIRECT") {
			F_DATE_VH0200.setValue();
			L_DATE_VH0200.setValue();
		}

		else if(info.value == "TODAY") {
			F_DATE_VH0200.setValue(todaysDate);
			L_DATE_VH0200.setValue(todaysDate);	
		}

		else if(info.value == "WEEK") {
			F_DATE_VH0200.setValue(weekAgoDate);
			L_DATE_VH0200.setValue(todaysDate);			
		}

		else if(info.value == "MONTH") {
			F_DATE_VH0200.setValue(firstday);
			L_DATE_VH0200.setValue(todaysDate);				
		}
	};
